(()=>{const e=document.getElementById("scan-button"),t=document.getElementById("url-text");e.addEventListener("click",(async function(){const[e]=await chrome.tabs.query({active:!0,lastFocusedWindow:!0});chrome.tabs.sendMessage(e.id,{action:"scan"})})),chrome.runtime.onMessage.addListener((function(e){"hidePopup"===e.action&&(document.body.style.display="none")})),chrome.runtime.onMessage.addListener((function(e){"finalQR"===e.action&&(document.body.style.display="block",t.innerText=e.data,t.href=`${e.data}`),"error"===e.action&&(alert("That is not a QR"),document.body.style.display="block")}))})();